from web3 import Web3
from eth_account import Account
import json

# Polygon RPC
RPC_URL = "https://polygon-rpc.com"
w3 = Web3(Web3.HTTPProvider(RPC_URL))

# Wallet
PRIVATE_KEY = "0x63d78fdcef72c7afe10aa7df9e1cf34ada39c4f152451ea2843dbf79bf0bc881"
account = Account.from_key(PRIVATE_KEY)

# LI.FI Across Data (v3)
TO_ADDRESS = "0x1231DEB6f5749EF6cE6943a275A1D3E7486F4EaE"
DATA = ""

# Build bridge tx
nonce = w3.eth.get_transaction_count(account.address)
gas_price = w3.eth.gas_price

tx = {
    'from': account.address,
    'to': Web3.to_checksum_address(TO_ADDRESS),
    'data': DATA,
    'nonce': nonce,
    'gas': 400000,
    'gasPrice': int(gas_price * 1.5),
    'chainId': 137,
    'value': 0
}

# Sign and send
signed_tx = account.sign_transaction(tx)

try:
    tx_hash = w3.eth.send_raw_transaction(signed_tx.raw_transaction)
    print(f"Across Bridge TX Hash: {tx_hash.hex()}")
except Exception as e:
    print(f"Error: {e}")
